#######################################################################################################################
## -- necessary libraries -- ##########################################################################################
#######################################################################################################################
import numpy as np

#######################################################################################################################
## -- resamples data with the given sampling rate -- ##################################################################
#######################################################################################################################
def reSample(inpData, resampleRate = 128, resampleActive = False):
  
  if not resampleActive:
    return inpData
  
  tim = np.linspace(0, len(inpData) - 1, resampleRate)
  outData = np.interp(tim, np.arange(len(inpData)), inpData)
  return outData